INSERT INTO `danhngon` (`iddn`, `noidung`) VALUES
(1, 'Bạn sẽ biết rõ một người hơn rất nhiều dựa vào những gì anh ta thể hiện vào những ngày cuối cùng của một mối quan hệ chứ không phải bởi những gì anh ta đã làm vào buổi đầu tiên. - Phan Ý Yên'),
(2, 'Thất bại lớn nhất bạn có thể gặp phải trong đời là mắc phải sai lầm không bao giờ cố gắng. - Khuyết danh'),
(3, 'Không tìm được lý do để tiếp tục kiên trì, vậy thì hãy tìm một lý do để bắt đầu lại mọi thứ, cuộc sống cũng chỉ đơn giản như vậy mà thôi. - Hòa Hỏa'),
(4, 'Niềm tin là sức mạnh có thể khiến thế giới tan vỡ xuất hiện trong ánh sáng. - Helen Keller'),
(5, 'Viên mãn nhất trong tình cảm không phải là nắm giữ được bàn tay của một người nhất nhất không rời, mà là khi trải qua bao nhiêu mất mát đổi thay, họ vẫn về để nắm lấy tay bạn… - Khuyết Danh');

